<script>
export default {
  name: "Icon",
  props:{
    icon:{
      type: String,
      validator(val){
        return ["ghost","respect", "rand", "soon", "progress", 'edit', 'expand'].includes(val)
      },
      default: "ghost",
    },
    styledLike:{
      type: String,
      validator(val){
        return ["default", "success", "warning", "danger", "description", "link"].includes(val)
      },
      default: "default"
    },
    size:{
      type: String,
      validator(val){
        return ["default", "button", "small"].includes(val)
      },
      default: "default"
    }
  }
}
</script>

<style scoped lang="scss">
svg {
  width: var(--icon-size);
  height: var(--icon-size);
  fill: none;

  &.button {
    height: 10px;
    width: 10px;
  }

  &.small{
    width: var(--icon-size-small);
    height: var(--icon-size-small);
  }
  path{
    fill: $icon-default;
  }

  &.success {
    path{
      fill: $success-font;
    }
  }

  &.warning {
    path{
      fill: $warning-font;
    }
  }

  &.danger {
    path{
      fill: $danger-font;
    }
  }

  &.description {
    path{
      fill: $description-font;
    }
  }

  &.link {
    path{
      fill: $link-light;
    }
  }

  path {
    &.blackout{
      fill: black !important;
    }
  }
}
</style>
